function getRegionLevels() {
  if(typeof regionArray != "undefined") {
    const regionArray = data.map(element => {
      return {
        id: element.id,
        name: element.name,
        description: element.description,
        order:  element.order
      }
    })
    return regionArray;
  } else {
    const regionArray = []
    return regionArray;
  }
}

function getRegions() {
  if(typeof regionsData != "undefined") {
    const regionArray = regionsData.map(element => {
        return {
          id: element.id,
          name: element.name,
          shortName: element.shortName,
          order: element.order,
          scenarioCollections: element.scenarioCollections
        }
    })
    return regionArray;
  } else {
    const regionArray = []
    return regionArray;
  }
}

function getScenarioCollection(id) {
  var scenarioCollection = [];
  regionsData.forEach(element => {
    if(element.id === id) {
      scenarioCollection = element.scenarioCollections;
    } 
  });
  scenarioCollection = scenarioCollection.map(element => {
      return {
        id: element.id,
        name: element.name,
        description: element.description
      }
  })
  return scenarioCollection;    
}

function getScenarios() {
  var scenario = scenariosAndTimeTables["0"].scenarios.map(element => {
      return {
        id: element.id,
        description: element.description,
        name: element.name,
        order: element.order
      }
  })
  return scenario;  
}

function getTimePeriods() {
  const timePeriods = scenariosAndTimeTables["0"].timePeriods.map(element => {
    return {
      id: element.id,
      yearStart: element.yearStart,
      yearEnd: element.yearEnd
    }
  })
  return timePeriods;
}

function getIndicatorCategories() {
  const indicatorCategories = scenariosAndTimeTables["0"].indicatorCategories.map(element => {
    return { 
      id: element.id,
      name: element.name,
      description: element.description,
      isMandatory: element.isMandatory,
      order: element.order,
      indicators: element.indicators,
    }
  })
  return indicatorCategories;
}

function getIndicators(id) {
  var indicatorCategory = getIndicatorCategories();
  indicatorCategory.forEach(element => {
    if(element.id === id) {
      indicatorCategory = element;
    } 
  });
  if(typeof indicatorCategory.indicators != "undefined") {
    var indicators = indicatorCategory.indicators.map(element => {
        return {
          id: element.id,
          name: element.name,
          description: element.description,
          absVar: element.absVar,
          order: element.order
        }
    })
  } else {
    var indicators = []
  }
  return indicators;
}

function createOptions(array){
  let options = [];
  array.forEach(element => {
      let value = element.id;
      let label = element.name;
      let singleObject = {value, label}
      options.push(singleObject);
  });
  return options
}

function createTimeOptions(array){
  let options = [];
  array.forEach(element => {
      let value = element.id;
      let label = element.yearStart + "-" + element.yearEnd;
      let singleObject = {value, label}
      options.push(singleObject);
  });
  return options
}

function createDescriptionAsNameOptions(array){
  let options = [];
  array.forEach(element => {
      let value = element.id;
      let label = element.description;
      let singleObject = {value, label}
      options.push(singleObject);
  });
  return options
}


export default {createTimeOptions, createDescriptionAsNameOptions, getRegionLevels, getRegions, getScenarios, getTimePeriods, getScenarioCollection, getIndicators, createOptions}